    color: hsl(var(--skd-rent));
  }

  .bg-skd-rent {
    background-color: hsl(var(--skd-rent));
  }

  .text-skd-events {
    color: hsl(var(--skd-events));
  }

  .bg-skd-events {
    background-color: hsl(var(--skd-events));
  }

  .text-skd-home {
    color: hsl(var(--skd-home));
  }

  .bg-skd-home {
    background-color: hsl(var(--skd-home));
  }

  .text-skd-co {
    color: hsl(var(--skd-co));
  }

  .bg-skd-co {
    background-color: hsl(var(--skd-co));
  }
}

@keyframes shimmer {
  0% { 
    background-position: -200px 0; 
  }
  100% { 
    background-position: calc(200px + 100%) 0; 
  }
}

@keyframes float {
  0%, 100% { 
    transform: translateY(0px); 
  }
  50% { 
    transform: translateY(-10px); 
  }
}

.animate-float {
  animation: float 3s ease-in-out infinite;
}

/* Additional utility classes */
.line-clamp-2 {
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

.line-clamp-3 {
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

/* Enhanced gradient backgrounds */
.gradient-gold {
  background: linear-gradient(135deg, #FFD700 0%, #FFA500 50%, #FF8C00 100%);
}

.gradient-pink {
  background: linear-gradient(135deg, #FF69B4 0%, #FF1493 50%, #DC143C 100%);
}

/* Service-specific hover effects */
.hover-lift {
  transition: transform 0.3s ease, box-shadow 0.3s ease;
}

.hover-lift:hover {
  transform: translateY(-5px);
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
}

/* Glassmorphism effect */
.glass-card {
  background: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.dark .glass-card {
  background: rgba(0, 0, 0, 0.4);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
  background: hsl(var(--gold));
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: hsl(var(--gold) / 0.8);
}

